function Home(props) {

const [GenresSelected] = useState ({
  token: false,
  action: false,
  comedy: false,
  romance: false,
  animation: false,
  history: false,
  musical: false
});

const [currentTitle, setCurrentTitle] = useState([]);
const [currentYear, setCurrentYear] = useState([]);
const [currentPoster, setCurrentPoster] = useState([]);


const [note, setNote] = useState({
  title: "",
  year: "",
  poster: ""
});

function handleChange(event) {
  const { name, value } = event.target;
  setNote(prevNote => {
    return {
      ...prevNote,
      [name]: value
    };
  });
}

function submitNote(event) {
  props.onAdd(note);
  setNote({
    title: currentTitle,
    content: currentYear
  });
  event.preventDefault();
  getOtherMovie();
}

function getOtherMovie(){
  // re-rendered the component
  //props.onAdd(note);
  fetch('/getmovie').then(response => response.json())
  .then(data => {
    setCurrentTitle(data.title);
    setCurrentYear(data.year);
    setCurrentPoster(data.poster);
    });
}

useEffect(() => {
    fetch('/generate',{
      method:"POST",
      headers: {'Content-Type': 'application/json',},
      body: JSON.stringify({Action:true,History:true,Comedy:true,Romance:true,SciFi:true,Musical:true,Animation:true,Horror:true})
    }).then(res => res.json()).then(data => {
    setCurrentTitle(data.title);
    setCurrentYear(data.year);
    setCurrentPoster(data.poster);
    });
}, []);

function Swipe(props) {
  return (

    <form className="create-note">
            
              <input
                name="title"
                onChange={handleChange}
                value={currentTitle}
                
              />
            

            <textarea
              name="content"
              onChange={handleChange}
              value={currentYear}  
            />
              <img src={currentPoster}/>
              <br/>
              <Fab style={{color: "green"}} onClick={submitNote}>
                <FavoriteIcon />
              </Fab>
              <Fab style={{color: "red"}} onClick={getOtherMovie}>
                <HeartBrokenIcon />
              </Fab>

          </form>

  );
}
    return (
      <div>

      <div>
           <Welcome/>
      </div>

        <div>
          <Genres />
        </div>

        <div>
          <Swipe />
        </div>
      </div>
      
      );
}


